mapper.select:
SELECT c.id as id ,c.typeid as typeid ,c.title as title ,c.summary as summary ,IFNULL(c.thumbs,' ') as thumbs ,c.readtimes as readtimes ,c.goodtimes as goodtimes ,c.updtime as add_time ,'毕业之家' as author FROM paper_news_content c WHERE 1 = 1
AND c.typeid = #{typeid}
ORDER BY c.readtimes DESC
ORDER BY c.updtime DESC
LIMIT #{startindex}, #{pagesize};

SELECT c.id as id ,c.typeid as typeid ,c.title as title ,c.summary as summary ,c.readtimes as readtimes ,c.goodtimes as goodtimes ,c.updtime as add_time ,'毕业之家' as author FROM paper_question_content c WHERE 1 = 1
AND c.typeid = #{typeid}
ORDER BY c.readtimes DESC
ORDER BY c.updtime DESC
LIMIT #{startindex}, #{pagesize};
