mapper.select:
SELECT c.id as questionid -- ,c.typeid as typeid ,c.title as title ,c.summary as summary -- ,c.thumbs as thumbs -- ,c.content as newsurl ,c.readtimes as readtimes ,c.goodtimes as thumbs ,c.updtime as updtime ,t.questiontype as questiontype FROM paper_question_content c LEFT JOIN paper_question_type t ON c.typeid = t.id WHERE 1 = 1
AND c.typeid = #{questiontype}
AND c.id = #{questionid}
ORDER BY c.updtime DESC
LIMIT #{startindex}, #{pagesize};

mapper.update:
UPDATE paper_question_content SET

    updtime = #{time}


    ,title = #{title}


    ,typeid = #{questiontypeid}


    ,summary = #{summary}


    ,content = #{content}

WHERE id = #{questionid}
